import React, { FC, useState } from 'react';

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link as RouterLink, 
  LinkProps as RouterLinkProps
} from "react-router-dom";

import { makeStyles } from '@material-ui/core/styles';

import Box from '@material-ui/core/Box';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import Link from '@material-ui/core/Link';

import Header from './layout/Header';
import LandingPage from './LandingPage';
import Footer from './layout/Footer';

import AboutUs from './company/AboutUs';
import { Legal } from './legal/Legal';

// import { isAuthenticated } from '../utils/auth';


const useStyles = makeStyles((theme) => ({
  marketing: {
    textAlign: 'center',
    display: 'flex',
    flexDirection: 'column',
    height: '100vh',
  },
  header: {
    display: 'flex',
    alignItems: 'center',
  },
  main: {
    display: 'flex',
    flexGrow: 1,
    justifyContent: 'center',
    fontSize: 'calc(10px + 2vmin)',
    //color: 'white',
  },

  '@global': {
    ul: {
        margin: 0,
        padding: 0,
        listStyle: 'none',
    },
},
footer: {
    borderTop: `1px solid ${theme.palette.divider}`,
    marginTop: theme.spacing(8),
    paddingTop: theme.spacing(3),
    paddingBottom: theme.spacing(3),
    [theme.breakpoints.up('sm')]: {
        paddingTop: theme.spacing(6),
        paddingBottom: theme.spacing(6),
    },
},
copyright: {
    alignItems: 'center',
    width: '100%',
},
}));


function Blog() {
  return (
    <div>
      <h2>Blog</h2>
    </div>
  );
}
function Features() {
  return (
    <div>
      <h2>Features</h2>
    </div>
  );
}
function Pricing() {
  return (
    <div>
      <h2>Pricing</h2>
    </div>
  );
}


function Copyright() {
  return (
      <Typography variant="body2" color="textSecondary" align="center">
          {'Copyright © '}
          <Link color="inherit" component={RouterLink} to="/">
              Kalamos Care
          </Link>
          {' '}
          {new Date().getFullYear()}
          {'.'}
      </Typography>
  );
};


const footers = [
  {
      title: 'Company',
      subheaders: [
          {
              description: 'About Us',
              url: '/company/about-us'
          },
          {
              description: 'Blog',
              url: '/company/blog'
          },
      ],
  },
  {
      title: 'Product',
      subheaders: [
          {
              description: 'Features',
              url: '/product/features'
          },
          {
              description: 'Pricing',
              url: '/product/pricing'
          },
      ],
  },
  {
      title: 'Resources',
      subheaders: [
          {
              description: 'FAQ',
              url: '#'
          },
          {
              description: 'API Docs',
              url: '/api/v1'
          },
      ],
  },
  {
      title: 'Legal',
      subheaders: [
          {
              description: 'Privacy policy',
              url: '/legal/privacy-policy'
          },
          {
              description: 'Terms of Use',
              url: '/legal/terms-of-use'
          },
      ],
  },
];

export const Home: FC = () => {
  const classes = useStyles();

  return (
    <Box className={classes.marketing}>
      <header className={classes.header}>
        <Header />
      </header>
      <main className={classes.main}>
          {/*
          A <Switch> looks through all its children <Route>
          elements and renders the first one whose path
          matches the current URL. Use a <Switch> any time
          you have multiple routes, but you want only one
          of them to render at a time
        */}
          <Switch>
            <Route exact path="/">
              <LandingPage />
            </Route>
            <Route exact path="/company/about-us">
              <AboutUs />
            </Route>
            <Route exact path="/company/blog">
              <Blog />
            </Route>
            <Route exact path="/product/features">
              <Features />
            </Route>
            <Route exact path="/product/pricing">
              <Pricing />
            </Route>
            <Route exact path="/resources/faq">
              <h2>FAQ</h2>
            </Route>
            <Route exact path="/resources/api">
              <h2>API Documentation</h2>
            </Route>
            <Route exact path="/legal">
              <Legal />
            </Route>
          </Switch>
        {/*<LandingPage />*/}
        {/*{children}*/}
      </main>
      <footer>
        {/*<Footer />*/}
        <Container maxWidth="md" component="footer" className={classes.footer}>
            <Grid container spacing={4} justify="space-evenly">
                <Router>
                {footers.map((footer) => (
                    <Grid item xs={6} sm={3} key={footer.title}>
                        <Typography variant="h6" color="textPrimary" gutterBottom>
                            {footer.title}
                        </Typography>
                        <ul>
                            {footer.subheaders.map((subheader) => (
                                <li key={subheader.description}>
                                    <Link variant="subtitle1" color="textSecondary" component={RouterLink} to={subheader.url}>
                                        {subheader.description}
                                    </Link>
                                </li>
                            ))}
                        </ul>
                    </Grid>
                ))}
                </Router>
            </Grid>
            <Box mt={5}>
                <Copyright />
            </Box>
        </Container>
      </footer>
    </Box>
  );
};
