import React, { FC } from 'react';
import {
    Switch,
    Route,
    Redirect,
    Link as RouterLink
  } from "react-router-dom";
import { makeStyles } from '@material-ui/core/styles';

import { PatientList } from './PatientList';
import { NewPatient } from './NewPatient';

import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import SpeedDial, { SpeedDialProps } from '@material-ui/lab/SpeedDial';
import SpeedDialIcon from '@material-ui/lab/SpeedDialIcon';
import SpeedDialAction from '@material-ui/lab/SpeedDialAction';

import NewOrderIcon from '@material-ui/icons/LocalHospital';
import NewPatientIcon from '@material-ui/icons/PersonAdd';


const useStyles = makeStyles((theme) => ({
    speedDial: {
        position: 'absolute',
        '&.MuiSpeedDial-directionUp, &.MuiSpeedDial-directionLeft': {
            bottom: theme.spacing(8),
            right: theme.spacing(2),
        },
    },
}));

const actions = [
    { icon: <NewPatientIcon component={RouterLink} to="/clinic/patients/new"/>, name: 'New Patient' },
    { icon: <NewOrderIcon component={RouterLink} to="/clinic/patients/"/>, name: 'New Order' },
];


export const Patients: FC = () => {
    const classes = useStyles();

    {/* Move all speeddial stuff to the main view and hide it when not on main list */ }
    const [direction, setDirection] = React.useState<SpeedDialProps['direction']>('up');
    const [open, setOpen] = React.useState(false);
    const [hidden, setHidden] = React.useState(false);


    const handleDirectionChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setDirection((event.target as HTMLInputElement).value as SpeedDialProps['direction']);
    };

    const handleHiddenChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setHidden(event.target.checked);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const handleOpen = () => {
        setOpen(true);
    };

    return (
        <>
            <Switch>
                <Route path="/clinic/patients">
                    <PatientList />
                </Route>
                <Route path="/clinic/patients/new">
                    <NewPatient />
                </Route>
            </Switch>

            <SpeedDial
                ariaLabel="PatientList Speeddial"
                className={classes.speedDial}
                icon={<SpeedDialIcon />}
                onClose={handleClose}
                onOpen={handleOpen}
                open={open}
                direction={direction}
            >
                {actions.map((action) => (
                    <SpeedDialAction
                        key={action.name}
                        icon={action.icon}
                        tooltipTitle={action.name}
                        onClick={handleClose}
                    />
                ))}
            </SpeedDial>
        </>
    );
};
