import React, { FC } from 'react';

import {
  Switch,
  Route
} from "react-router-dom";

import { makeStyles } from '@material-ui/core/styles';

import Box from '@material-ui/core/Box';

import { SignUp } from '../SignUp';
import { Login } from '../Login';

import LandingPage from './LandingPage';
import AboutUs from '../company/AboutUs';
import { Legal } from '../legal/Legal';

// import { isAuthenticated } from '../utils/auth';


const useStyles = makeStyles((theme) => ({
  marketing: {
    textAlign: 'center',
    display: 'flex',
    flexDirection: 'column',
    height: '100vh',
  },
  header: {
    display: 'flex',
    alignItems: 'center',
  },
  main: {
    display: 'flex',
    flexGrow: 1,
    justifyContent: 'center',
    fontSize: 'calc(10px + 2vmin)',
    //color: 'white',
  },
}));


function Blog() {
  return (
    <div>
      <h2>Blog</h2>
    </div>
  );
}
function Features() {
  return (
    <div>
      <h2>Features</h2>
    </div>
  );
}
function Pricing() {
  return (
    <div>
      <h2>Pricing</h2>
    </div>
  );
}


export const Main: FC = () => {
  const classes = useStyles();

  return (
    <Box className={classes.main}>
          {/*
          A <Switch> looks through all its children <Route>
          elements and renders the first one whose path
          matches the current URL. Use a <Switch> any time
          you have multiple routes, but you want only one
          of them to render at a time
        */}
          <Switch>
            <Route exact path="/">
              <LandingPage />
            </Route>
            <Route exact path="/signup">
              <SignUp />
            </Route>
            <Route exact path="/login">
              <Login />
            </Route>
            <Route exact path="/company/about-us">
              <AboutUs />
            </Route>
            <Route exact path="/company/blog">
              <Blog />
            </Route>
            <Route exact path="/product/features">
              <Features />
            </Route>
            <Route exact path="/product/pricing">
              <Pricing />
            </Route>
            <Route exact path="/resources/faq">
              <h2>FAQ</h2>
            </Route>
            <Route exact path="/resources/api">
              <h2>API Documentation</h2>
            </Route>
            <Route exact path="/legal">
              <Legal />
            </Route>
          </Switch>

    </Box>
  );
};
