import React, { FC } from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  useParams,
  Link
} from "react-router-dom";
import { useHistory } from 'react-router';
import { makeStyles } from '@material-ui/core/styles';

import {
  AppBar, BottomNavigation, BottomNavigationAction, Box, Container, IconButton, Menu, MenuItem, Toolbar, Typography
} from '@material-ui/core/';
// import Link from '@material-ui/core'
import {
  AccountCircle, Chat, Folder, Notifications
} from '@material-ui/icons/';

import { NotificationsList } from './Notifications/NotificationsList';
import { Patients } from './Patients/Patients';
import { Messages } from './Messages/Messages';

import Header from './layout/Header';

// import Main from './layout/Main';
import Footer from './layout/Footer';


const useStyles = makeStyles((theme) => ({
  app: {
    textAlign: 'center',
    display: 'flex',
    flexDirection: 'column',
    height: '100vh',
  },
  header: {
    display: 'flex',
    alignItems: 'center',
  },
  main: {
    display: 'flex',
    flexGrow: 1,
    justifyContent: 'center',
    fontSize: 'calc(10px + 2vmin)',
    //color: 'white',
  },
  footer: {
    display: 'flex',
    position: 'sticky',
    bottom: 0,
  },
  bottom_nav: {
    width: '100%',
  },
  copyright: {
    alignItems: 'center',
    width: '100%',
  },
  title: {
    flexGrow: 1,
    textAlign: "left",
  },
}));


export const Dashboard: FC = ({ children }) => {
  const classes = useStyles();
  const history = useHistory();

  const [auth, setAuth] = React.useState(true);

  // I think this is meant to log the user out?
  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setAuth(event.target.checked);
  };

  const [anchorEl, setAnchorEl] = React.useState<null | HTMLElement>(null);
  const open = Boolean(anchorEl);

  const handleAppBarMenu = (event: React.MouseEvent<HTMLElement>) => {
    setAnchorEl(event.currentTarget);
  };

  const handleAppBarMenuClose = () => {
    setAnchorEl(null);
  };

  // Bottom Nav state management (I think)
  const [value, setValue] = React.useState('notifications');

  function handleBottomNavChange(event: React.ChangeEvent<{}>, newValue: string) {
    setValue(newValue);
  }

  return (
    <Box className={classes.app}>
      <header className={classes.header}>
        <Header />
      </header>
      <main className={classes.main}>
        <Container>
          <Switch>
            <Route path="/clinic/notifications">
              <NotificationsList />
            </Route>
            <Route path="/clinic/patients">
              <Patients />
            </Route>
            <Route path="/clinic/messages">
              <Messages />
            </Route>
          </Switch>
        </Container>
      </main>
      <footer className={classes.footer}>
        <Footer />
      </footer>
    </Box>
  );
};
