import React, { FC } from 'react';
import { Switch, Route } from 'react-router-dom';
import { useHistory } from 'react-router';
import { makeStyles } from '@material-ui/core/styles';
// import Typography from '@material-ui/core/Typography'
import Container from '@material-ui/core/Container';
import Paper from '@material-ui/core/Paper'

import { Home, Login, SignUp, Protected, PrivateRoute } from '../views';
import { logout, isAuthenticated } from '../utils/auth';

const useStyles = makeStyles((theme) => ({
  container: {

  }
}));

const Body: FC = () => {
  const classes = useStyles();
  const history = useHistory();

  return (
    <Container maxWidth="md" className={classes.container}>
      <Switch>
        <Route path="/login" component={Login} />
        <Paper><Route path="/signup" component={SignUp} /></Paper>
        <Paper>
          <Route
            path="/logout"
            render={() => {
              logout();
              history.push('/');
              return null;
            }}
          />
        </Paper>
        <Paper><PrivateRoute path="/protected" component={Protected} /></Paper>
        <Paper><Route exact path="/" component={Home} /></Paper>
      </Switch>
    </Container>
  );
};

export default Body
