import React, { FC, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import Card from '@materiall-ui/core/Card';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';

import Header from '../layout/Header';

import { getMessage } from '../utils/api';
import { isAuthenticated } from '../utils/auth';


const useStyles = makeStyles((theme) => ({
  link: {
    color: '#ff6e6c',
  },
  body: {
    display: 'flex',
    flexWrap: 'wrap',
    '& > *': {
      margin: theme.spacing(1),
      width: theme.spacing(16),
      height: theme.spacing(16),
    },
  },
}));

export const Home: FC = () => {
  const [message, setMessage] = useState<string>('');
  const [error, setError] = useState<string>('');
  const classes = useStyles();

  const queryBackend = async () => {
    try {
      const message = await getMessage();
      setMessage(message);
    } catch (err) {
      setError(err);
    }
  };

  return (
    <>
      <Header />
      <Container className={classes.body}>
        <Grid>
        <Paper>
          {!message && !error && (
            <a className={classes.link} href="#" onClick={() => queryBackend()}>
              Click to make request to backend
            </a>
          )}
          {message && (
            <p>
              <code>{message}</code>
            </p>
          )}
          {error && (
            <p>
              Error: <code>{error}</code>
            </p>
          )}
        </Paper>
        </Grid>
        <Grid>
        <Paper>
          <a className={classes.link} href="/admin">
            Admin Dashboard
          </a>
        </Paper>
        </Grid>
        <Grid>
        <Paper>
          <a className={classes.link} href="/protected">
            Protected Route
      </a>
        </Paper>
        </Grid>
        <Grid>
        <Paper>
          {isAuthenticated() ? (
            <a className={classes.link} href="/logout">
              Logout
            </a>
          ) : (
              <>
                <a className={classes.link} href="/login">
                  Login
          </a>
                <a className={classes.link} href="/signup">
                  Sign Up
          </a>
              </>
            )}
        </Paper>
        </Grid>
      </Container>
    </>
  );
};
