import React, { FC, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
// import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';

import { getMessage } from '../utils/api';
import { isAuthenticated } from '../utils/auth';


const useStyles = makeStyles((theme) => ({
  link: {
    color: '#ff6e6c',
  },
  body: {
    display: 'flex',
    flexWrap: 'wrap',
    '& > *': {
      margin: theme.spacing(1),
      width: theme.spacing(16),
      height: theme.spacing(16),
    },
  },
  card: {
    minWidth: 275,
  },
}));

export const Home: FC = () => {
  const [message, setMessage] = useState<string>('');
  const [error, setError] = useState<string>('');
  const classes = useStyles();

  const queryBackend = async () => {
    try {
      const message = await getMessage();
      setMessage(message);
    } catch (err) {
      setError(err);
    }
  };

  return (
    <>
      <Container className={classes.body}>
        <Grid item xs={12}>
        <Card className={classes.card}>
                      {!message && !error && (
              <a className={classes.link} href="#" onClick={() => queryBackend()}>
                Click to make request to backend
              </a>
            )}
            {message && (
              <p>
                <code>{message}</code>
              </p>
            )}
            {error && (
              <p>
                Error: <code>{error}</code>
              </p>
            )}
          </Card>
        </Grid>
        <Grid item xs={12}>
        <Card className={classes.card}>
                      <a className={classes.link} href="/admin">
              Admin Dashboard
          </a>
          </Card>
        </Grid>
        <Grid item xs={12}>
        <Card className={classes.card}>
                      <a className={classes.link} href="/protected">
              Protected Route
      </a>
          </Card>
        </Grid>
        <Grid item xs={12}>
          <Card className={classes.card}>
            <CardActionArea>
              <CardContent>
                <Typography gutterBottom variant="h5" component="h2">
                  Authentication
    </Typography>
                <Typography variant="body2" color="textSecondary" component="p">
                  Test the APIs authentication below!
    </Typography>
              </CardContent>
            </CardActionArea>
            <CardActions>
              {isAuthenticated() ? (
                <Button size="small" color="primary">
                  <a className={classes.link} href="/logout">
                    Logout
                </a>
                </Button>
              ) : (
                  <>
                    <Button size="small" color="primary">
                      <a className={classes.link} href="/login">
                        Login
          </a>
                    </Button>
                    <Button size="small" color="primary">
                      <a className={classes.link} href="/signup">
                        Sign Up
          </a>
                    </Button>
                  </>
                )}
            </CardActions>
          </Card>
        </Grid>
      </Container>
    </>
  );
};
